targets.expr:
sum by(service_name) (
  rate(traces_span_metrics_calls_total{
    service_name="$service_name",
    span_kind="SPAN_KIND_SERVER",
    span_name="$endpoint"
  }[$__rate_interval])
) * 60

histogram_quantile(0.99,
  sum by (le) (
    rate(traces_span_metrics_duration_milliseconds_bucket{
      service_name="$service_name",
      span_kind="SPAN_KIND_SERVER",
      span_name="$endpoint"
    }[$__rate_interval])
  )
)

